import React from 'react';
import useInputState from './hooks/useInputState';
import './NoteForm.css';

function NoteForm ({ addNote }){
	const [
		value,
		handleChange,
		reset
	] = useInputState('');
	return (
		<form
			onSubmit={(e) => {
				e.preventDefault();
				addNote(value);
				reset();
			}}
			className="Notecard"
		>
			<input value={value} onChange={handleChange} label="Add New Todo" className="Notecard-input" />
		</form>
	);
}
export default NoteForm;
