import React from 'react';
import useInputState from './hooks/useInputState';
import './EditNoteForm.css';

function EditNoteForm ({ id, note, editNote, toggleEditForm }){
	const [
		value,
		handleChange,
		reset
	] = useInputState(note);
	return (
		<form
			onSubmit={(e) => {
				e.preventDefault();
				editNote(id, value);
				reset();
				toggleEditForm();
			}}
			style={{ marginLeft: '1rem', width: '50%' }}
		>
			<input value={value} onChange={handleChange} className="Notecard" />
		</form>
	);
}
export default EditNoteForm;
