import React, { useEffect } from 'react';
import NoteList from './NoteList';
import NoteForm from './NoteForm';
import useNoteState from './hooks/useNoteState';

function NotePanel (){
	const initialNotes = JSON.parse(window.localStorage.getItem('notes') || '[]');
	const { notes, removeNote, toggleNote, editNote } = useNoteState(initialNotes);

	useEffect(
		() => {
			window.localStorage.setItem('notes', JSON.stringify(notes));
		},
		[
			notes
		]
	);

	return (
		<div className="d-flex text-center justify-content-center card">
			<div className="card-body">
				<NoteList notes={notes} removeNote={removeNote} toggleNote={toggleNote} editNote={editNote} />
			</div>
		</div>
	);
}
export default NotePanel;
