import { useState } from 'react';
import uuid from 'uuid/v4';
export default (initialNotes) => {
	const [
		notes,
		setNotes
	] = useState(initialNotes);
	return {
		notes,
		addNote: (newNoteText) => {
			setNotes([
				...notes,
				{ id: uuid(), note: newNoteText, completed: false }
			]);
		},
		removeNote: (noteId) => {
			//filter out removed todo
			const updatedNotes = notes.filter((note) => note.id !== noteId);
			//call setNotes with new notes array
			setNotes(updatedNotes);
		},
		toggleNote: (noteId) => {
			const updatedNotes = notes.map(
				(note) => (note.id === noteId ? { ...notes, completed: !note.completed } : notes)
			);
			setNotes(updatedNotes);
		},
		editNote: (noteId, newNote) => {
			const updatedNotes = notes.map((note) => (note.id === noteId ? { ...note, note: newNote } : note));
			setNotes(updatedNotes);
		}
	};
};
