import React from 'react';
import useToggleState from './hooks/useToggleState';
import EditNoteForm from './EditNoteForm';

function Note ({ id, note, completed, removeNote, toggleNote, editNote }){
	const [
		isEditing,
		toggle
	] = useToggleState(false);
	return (
		<div>
			{isEditing ? (
				<EditNoteForm editNote={editNote} id={id} note={note} toggleEditForm={toggle} />
			) : (
				<div>
					<div> {note}</div>
					<button onClick={() => toggleNote(id)}>Toggle</button>
					<button aria-label="Delete" onClick={() => removeNote(id)}>
						Delete
					</button>
					<button aria-label="Edit" onClick={toggle}>
						Edit
					</button>
				</div>
			)}
		</div>
	);
}

export default Note;
