import React from 'react';
import useToggleState from './hooks/useToggleState';
import EditNoteForm from './EditNoteForm';

function Note ({ id, note, completed, removeNote, toggleNote, editNote }){
	const [
		isEditing,
		toggle
	] = useToggleState(false);
	return (
		<div>
			{isEditing ? (
				<EditNoteForm editNote={editNote} id={id} note={note} toggleEditForm={toggle} />
			) : (
				<div class="card">
					<div class="card-text m-3 text-left"> {note}</div>
					<div class="row justify-content-center">
						<button class="btn btn-primary m-1" onClick={() => toggleNote(id)}>
							Toggle
						</button>
						<button class="btn btn-primary m-1" aria-label="Delete" onClick={() => removeNote(id)}>
							Delete
						</button>
						<button class="btn btn-primary m-1" aria-label="Edit" onClick={toggle}>
							Edit
						</button>
					</div>
				</div>
			)}
		</div>
	);
}

export default Note;
