import React, { useEffect } from 'react';
import useNoteState from './hooks/useNoteState';
import SmallCardPanel from './SmallCardPanel';
import BigCard from './BigCard';
import NotePanel from './NotePanel';
import NoteForm from './NoteForm';
import Navbar from './Navbar';
import './App.css';

function App (){
	const initialNotes = JSON.parse(window.localStorage.getItem('notes') || '[]');
	const { notes, addNote, removeNote, toggleNote, editNote } = useNoteState(initialNotes);

	useEffect(
		() => {
			window.localStorage.setItem('notes', JSON.stringify(notes));
		},
		[
			notes
		]
	);
	return (
		<div className="App">
			<Navbar />
			<div className="container-fluid">
				<div className="row">
					<div className="col-9">
						<SmallCardPanel />
						<div className="row">
							<div className="col-4">
								<BigCard />
							</div>
							<div className="col-8">
								<NoteForm notes={notes} addNote={addNote} />
							</div>
						</div>
					</div>
					<div className="col-3 pr-0">
						<NotePanel notes={notes} removeNote={removeNote} toggleNote={toggleNote} editNote={editNote} />
					</div>
				</div>
			</div>
		</div>
	);
}

export default App;
