import React from 'react';
import useInputState from './hooks/useInputState';
import './NoteForm.css';

function NoteForm ({ addNote }){
	const [
		value,
		handleChange,
		reset
	] = useInputState('');
	return (
		<form
			onSubmit={(e) => {
				e.preventDefault();
				addNote(value);
				reset();
			}}
			className="Notecard align-self-start"
		>
			<div class="form-row">
				<label for="newNote" />
				<textarea
					id="newNote"
					placeholder="New Note"
					class="form-control"
					value={value}
					onChange={handleChange}
					label="Add New Todo"
					className="Notecard-new"
				/>
			</div>
			<div class="form-row">
				<button type="submit" class="btn btn-primary">
					Confirm identity
				</button>
			</div>
		</form>
	);
}
export default NoteForm;
